function build_line_chart(e){var t={type:"line",data:{labels:["Unfinished","Almost","Finished"],datasets:[{label:"resolution finishing this year",backgroundColor:"rgb(255, 99, 132)",borderColor:"rgb(255, 99, 132)",data:e}]},options:{scales:{y:{min:0,ticks:{stepSize:1}}}}};return new Chart(document.getElementById("resolution-finishing-line-chart"),t)}function build_bar_chart(e){var t={type:"bar",data:{labels:["Unfinished","Almost","Finished"],datasets:[{label:"resolution finishing this year",backgroundColor:"rgb(255, 99, 132)",borderColor:"rgb(255, 99, 132)",data:e}]},options:{scales:{y:{ticks:{stepSize:1}}}}};return new Chart(document.getElementById("resolution-finishing-bar-chart"),t)}function create_bar(){var e=window.location.origin+"/api/resolution/",t=new XMLHttpRequest;t.onreadystatechange=function(){if(4==this.readyState){var e=JSON.parse(this.responseText),t=0,n=0,i=0;e.data.forEach(function(e){e.percentage<60?i+=1:e.percentage<100?t+=1:n+=1}),build_line_chart([i,t,n]),build_bar_chart([i,t,n])}},t.open("GET",e,!0),t.send()}
